import React from 'react';
import ReactDOM from 'react-dom';
import Modal from 'react-modal';
import './addMail.css';

const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-20%',
    transform             : 'translate(-50%, -50%)',
    height                : '95%',
    width                 : '95%'
  }
};

class AddMail extends React.Component {
  constructor() {
    super();
 
    this.state = {
      modalIsOpen: false,
     
      nameInput: "",
      numInput: "",
      tagsInput: "", 
      list: []
    };
    this.addMail=this.addMail.bind(this);
    this.openModal = this.openModal.bind(this);
    this.afterOpenModal = this.afterOpenModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
    this.clearModal = this.clearModal.bind(this);
  }
 
  openModal() {
    this.setState({modalIsOpen: true});
  }
  changeNameInput(input) {
    this.setState({
      nameInput: input,
    });
  }
  changeNumInput(input) {
    
    this.setState({
      numInput: input,
    });
  }
  changeTagsInput(input) {
    

    this.setState({
      tagsInput: input,
    });
  }
 
  afterOpenModal() {
    // references are now sync'd and can be accessed.
    this.subtitle.style.color = '#f00';
  }
 
  closeModal() {
    this.setState({modalIsOpen: false});
  }

  clearModal(){

  }

  addMail(){
    let inputArray = this.state.list;
    let found = 0;
    
    for (let n=0; n<inputArray.length; n++){
      if(this.state.nameInput===inputArray[n][0]){
        inputArray[n][1]=parseInt(this.state.numInput)+parseInt(inputArray[n][1]);
        found=1;
        break;
      }
    }
    if (found==0) {
      inputArray.push([this.state.nameInput,this.state.numInput]);
    };
    
    
    
  }
  

  remove(val){
    let inputArray = this.state.list;
    inputArray = inputArray.filter(e => e !== val);
    this.setState({
      list: inputArray,
      userInput: ""
    });
  }

 
 
  render() {
    return (
      <div>
        
        <button onClick={this.openModal}>Add Mail</button>
        
        <ul>
          
        </ul>


        <table style={{width: "100%"}}>
          <tr>
            <th>Name</th>
            <th>How Much Mail?</th> 
            <th>Tags</th>
          </tr>

          {this.state.list.map(val => (
            <tr>
              <td>{val[0]}</td>
              <td>{val[1]}</td>
              <td>{val[1]}</td>
              <button
                onClick={() => {
                  this.remove(val);
                }}
              >
                X
              </button>
              {/* <button
                onClick={() => {
                  this.edit(val);
                }}
              >
                Edit
              </button> */}
            </tr>
          ))}
          
          
        </table>


        <Modal
          isOpen={this.state.modalIsOpen}
          onAfterOpen={this.afterOpenModal}
          onRequestClose={this.closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          
          <h2 ref={subtitle => this.subtitle = subtitle}>Hello</h2>
          <button onClick={this.closeModal}>X</button>
          <div>Who got mail?</div>
          <form >
          <select id='nameInput' name="People" value={this.state.nameInput} onChange={e => {
            this.changeNameInput(e.target.value);
          }}>
           <option value="" disabled selected>    </option>
           <option value="kevin">Kevin</option>
           <option value="jony">Jony</option>
           <option value="dario">Dario</option>
           <option value="mom">Mom</option>
          </select><br></br>
          <br></br>
          
            <p>How much mail?</p><input id="numInput" value={this.state.numInput} type="number" min="0" onChange={e => { this.changeNumInput(e.target.value); }}/><br></br>
            <br></br>
            <div>

            <ul class="ks-cboxtags" >
              <li><input type="checkbox" id="checkboxOne" value="Spam" ></input><label for="checkboxOne">Spam</label></li>
              <li><input type="checkbox" id="checkboxTwo" value="Bill"></input><label for="checkboxTwo">Bill</label></li>
              <li><input type="checkbox" id="checkboxThree" value="Package"></input><label for="checkboxThree">Package</label></li>
              <li><input type="checkbox" id="checkboxFour" value="Other" ></input><label for="checkboxFour">Other</label></li>
              <input id="name2" name="name2" disabled="disabled"/>

            </ul>
            </div>
           
            <button type='reset' onClick={this.addMail}>Add</button>
            <button type='reset' >Reset</button>
          </form>
        </Modal>
      </div>
    );
  }
}



export default AddMail;
